mapper.select:
select
    (select
        if(count(*) > 0, 1, 0)
    from
        user_tb
    where
        email = #{email}
) + (
    select
        if(count(*) > 0, 2, 0)
    from
        user_tb
    where
        nickname = #{nickname}
) as result

mapper.update:
update
    user_tb
set
    profile_url = #{profileUrl}
where
    user_id = #{userId}

update
    user_tb
set
    password = #{password}
where
    email = #{email}
